mapper.select:
SELECT b.user_id, b.faction_id, SUM(benefit_money) AS benefit
FROM torn_faction_oc_benefit b
WHERE b.deleted = 0 AND b.faction_id = #{factionId} AND b.oc_finish_time BETWEEN #{fromDate} AND #{toDate}
GROUP BY b.user_id, b.faction_id
ORDER BY benefit DESC
LIMIT 30;

SELECT t.user_id, t.faction_id, SUM(benefit_money) AS benefit
FROM (
    SELECT b.user_id, b.faction_id, b.benefit_money
    FROM torn_faction_oc_benefit b
    WHERE b.deleted = 0 AND b.faction_id = #{factionId} AND b.oc_finish_time BETWEEN #{fromDate} AND #{toDate}
      AND b.oc_name NOT IN

          #{item}


    UNION ALL

    SELECT s.user_id, s.faction_id, s.final_income AS benefit_money
    FROM torn_faction_oc_income_summary s
    WHERE s.deleted = 0 AND s.faction_id = #{factionId} AND s.year_month = #{yearMonth}
) t
GROUP BY t.user_id, t.faction_id
ORDER BY benefit DESC
LIMIT 30;

SELECT t.user_id, t.faction_id, SUM(benefit_money) AS benefit FROM ( -- 大锅饭帮派的非大锅饭OC SELECT b.user_id, b.faction_id, b.benefit_money FROM torn_faction_oc_benefit b WHERE b.deleted = 0 AND b.faction_id IN
#{item}
AND b.oc_finish_time BETWEEN #{fromDate} AND #{toDate} AND b.oc_name NOT IN
#{item}
UNION ALL -- 非大锅饭帮派的OC SELECT b.user_id, b.faction_id, b.benefit_money FROM torn_faction_oc_benefit b WHERE b.deleted = 0 AND b.oc_finish_time BETWEEN #{fromDate} AND #{toDate} AND b.faction_id NOT IN
#{item}
UNION ALL -- 大锅饭帮派的大锅饭OC SELECT s.user_id, s.faction_id, s.final_income AS benefit_money FROM torn_faction_oc_income_summary s WHERE s.deleted = 0 AND s.year_month = #{yearMonth} AND s.faction_id IN
#{item} ) t GROUP BY t.user_id, t.faction_id ORDER BY benefit DESC LIMIT 50
